wwv_flow_imp_page.create_page_plug(
 p_id=>wwv_flow_imp.id(168114851528553223787)
,p_plug_name=>'BOM'
,p_title=>'Laptop BOM'
,p_region_name=>'tree_bom'
,p_parent_plug_id=>wwv_flow_imp.id(168114851752422223789)
,p_region_template_options=>'#DEFAULT#:t-Region--scrollBody'
,p_region_attributes=>'style="width: 350px"'
,p_plug_template=>4072358936313175081
,p_plug_display_sequence=>10
,p_plug_display_point=>'SUB_REGIONS'
,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with bom_hierarchy (item_id, parent_item_id, lvl, item_name, part_number, item_type, relation_id, ids_json) as (',
'    -- Anchor: top-level assemblies',
'    select ',
'        i.item_id,',
'        null as parent_item_id,',
'        1 as lvl,',
'        i.item_name,',
'        i.part_number,',
'        i.item_type,',
'        null as relation_id,',
'        json_object(',
'            ''itemId'' value i.item_id,',
'            ''relationId'' value null',
'        ) as ids_json           ',
'    from bom_item i',
'    where i.item_type = ''ASSEMBLY''',
'',
'    union all',
'',
'    -- Recursive: add children',
'    select ',
'        c.item_id,',
'        bh.item_id as parent_item_id,  -- parent in hierarchy',
'        bh.lvl + 1,',
'        c.item_name,',
'        c.part_number,',
'        c.item_type,',
'        r.relation_id,',
'        json_object(',
'            ''itemId'' value c.item_id,',
'            ''relationId'' value r.relation_id',
'        ) as ids_json        ',
'    from bom_relation r',
'         join bom_hierarchy bh on bh.item_id = r.parent_item_id',
'         join bom_item c on c.item_id = r.child_item_id',
')',
'select ',
'    item_id as id,',
'    relation_id,',
'    parent_item_id as parent_id,',
'    item_name || '' ('' || part_number || '')'' as label,',
'    case ',
'        when item_type = ''ASSEMBLY'' then ''fa fa-laptop''',
'        when item_type = ''SUBASSEMBLY'' then ''fa fa-cubes''',
'        when item_type = ''COMPONENT'' then ''fa fa-microchip''',
'    end as icon,',
'    ids_json',
'from bom_hierarchy',
'order by lvl, item_name;',
''))
,p_lazy_loading=>false
,p_plug_source_type=>'NATIVE_JSTREE'
,p_ai_enabled=>false
,p_attributes=>wwv_flow_t_plugin_attributes(wwv_flow_t_varchar2(
  'activate_node_link_with', 'S',
  'default_icon_css_class', 'icon-tree-folder',
  'icon_type_css_class', 'a-Icon',
  'node_id_column', 'ID',
  'node_label_column', 'LABEL',
  'node_value_column', 'IDS_JSON',
  'parent_key_column', 'PARENT_ID',
  'start_tree_with', 'NULL',
  'tree_hierarchy', 'SQL',
  'tree_tooltip', 'N')).to_clob
);
